BEGIN TRANSACTION;
CREATE TABLE "schema_migrations" ("version" text NOT NULL);
CREATE TABLE "movies" ("id" SERIAL PRIMARY KEY NOT NULL, "title" text, "description" text, "director" text, "actors" text, "genre" text, "runtime" text, "budget" text, "created_at" timestamp, "updated_at" timestamp, "cover_file_name" text, "cover_content_type" text, "cover_file_size" integer, "cover_updated_at" timestamp);
CREATE TABLE "users" ("id" SERIAL PRIMARY KEY NOT NULL, "email" text DEFAULT '' NOT NULL, "encrypted_password" text DEFAULT '' NOT NULL, "reset_password_token" text, "reset_password_sent_at" timestamp, "remember_created_at" timestamp, "sign_in_count" integer DEFAULT 0 NOT NULL, "current_sign_in_at" timestamp, "last_sign_in_at" timestamp, "current_sign_in_ip" text, "last_sign_in_ip" text, "created_at" timestamp, "updated_at" timestamp);
CREATE TABLE "reviews" ("id" SERIAL PRIMARY KEY NOT NULL, "body" text, "star" integer, "user_id" integer, "movie_id" integer, "created_at" timestamp, "updated_at" timestamp);
CREATE TABLE "tags" ("id" SERIAL PRIMARY KEY NOT NULL, "name" text, "created_at" timestamp, "updated_at" timestamp);
CREATE TABLE "taggings" ("id" SERIAL PRIMARY KEY NOT NULL, "tag_id" integer, "movie_id" integer, "created_at" timestamp, "updated_at" timestamp);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE UNIQUE INDEX "index_users_on_email" ON "users" ("email");
CREATE UNIQUE INDEX "index_users_on_reset_password_token" ON "users" ("reset_password_token");
CREATE INDEX "index_reviews_on_user_id" ON "reviews" ("user_id");
CREATE INDEX "index_reviews_on_movie_id" ON "reviews" ("movie_id");
CREATE INDEX "index_taggings_on_tag_id" ON "taggings" ("tag_id");
CREATE INDEX "index_taggings_on_movie_id" ON "taggings" ("movie_id");
COMMIT;
